import React, { Component, useState } from 'react';

const Test = ({questions}) => (
  <div>
    {questions.map((question, index) => (
      <Question {...question} id={index} key={index} />
    ))}
  </div>
)

const Question = ({id, text, answers}) => {
  const [chosen, updateChosen] = useState(-1);
  return (
    <div>
      <p>{text}</p>
      {answers.map((answer, index) => (
        <div>
          <label>
            <input type="radio" checked={index === chosen} name={text + id} onChange={()=>updateChosen(index)}/>
            {answer}
          </label>
        </div>
        )
      )}
    </div>
  )
}

const questions = [
  {
    text: "first",
    answers: [
      "a",
      "b",
      "c"
    ]
  },
  {
    text: "second",
    answers: [
      "d",
      "e",
      "f"
    ]
  },
  {
    text: "third",
    answers: [
      "g",
      "h",
      "i"
    ]
  },
]

class App extends Component {
  render() {
    return (
      <Test questions={questions} />
    );
  }
}

export default App;
