import React, { Component, useState } from 'react';

const Test = ({questions}) => {
  const initialAnswers = questions.map(() => -1);
  const [answers, setAnswers] = useState(initialAnswers);
  return (
    <div>
      {questions.map((question, index) => (
        <Question
          {...question}
          chosen={answers[index]}
          setAnswer={(answer) => setAnswers([...answers.slice(0, index), answer, answers.slice(index + 1)])}
        />
      ))}
      <button onClick={() => alert("submit")}>Submit</button>
    </div>
  )
}

const Question = ({id, text, answers, chosen, setAnswer}) => {
  return (
    <div>
      <p>{text}</p>
      {answers.map((answer, index) => (
        <div>
          <label>
            <input type="radio" checked={index === chosen} name={text + id} onChange={() => setAnswer(index)}/>
            {answer}
          </label>
        </div>
        )
      )}
    </div>
  )
}

const questions = [
  {
    text: "first",
    id: 1,
    answers: [
      "a",
      "b",
      "c"
    ]
  },
  {
    text: "second",
    id: 2,
    answers: [
      "d",
      "e",
      "f"
    ]
  },
  {
    text: "third",
    id: 3,
    answers: [
      "g",
      "h",
      "i"
    ]
  },
]

class App extends Component {
  render() {
    return (
      <Test questions={questions} />
    );
  }
}

export default App;
