import React, { Component, useState } from 'react';
import logo from './logo.svg';
import './App.css';

const Question = ({text, answers}) => {
  const [chosen, updateChosen] = useState(0);
  return (
    <>
      <p>{text}</p>
      {answers.map((answer, index) => (
        <>
          <label>
            <input type="radio" checked={index === chosen} name={text} onChange={()=>updateChosen(index)}/>
            {answer}
          </label>
        </>
        )
      )}
    </>
  )
}


class App extends Component {
  render() {
    return (
      <Question text="text" answers={["a", "b", "c"]} />
    );
  }
}

export default App;
