import React, { Component, useState } from 'react';

const Test = ({questions}) => (
  <>
    {questions.map((question, index) => (
      <Question {...question} id={index} key={index} />
    ))}
  </>
)

const Question = ({id, text, answers}) => {
  const [chosen, updateChosen] = useState(0);
  return (
    <>
      <p>{text}</p>
      {answers.map((answer, index) => (
        <>
          <label>
            <input type="radio" checked={index === chosen} name={text + id} onChange={()=>updateChosen(index)}/>
            {answer}
          </label>
        </>
        )
      )}
    </>
  )
}


class App extends Component {
  render() {
    return (
      <Question text="text" answers={["a", "b", "c"]} />
    );
  }
}

export default App;
