import React, { Component, useState } from 'react';

// TODO: refactor stateless
class Test extends Component {
  state = {
    questions: this.props.questions,
    answers: this.props.questions.map(() => -1),
  }

  setAnswers = (answers) => {
    console.log(answers);
    this.setState({answers})
  };

  render () {
    const { questions, answers } = this.state;
    return (
      <div>
        {questions.map((question, index) => (
          <Question
            key={index}
            {...question}
            chosen={answers[index]}
            setAnswer={(answer) => this.setAnswers([...answers.slice(0, index), answer, ...answers.slice(index + 1)])}
          />
        ))}
        <button onClick={() => this.props.onSubmit(answers)}>Submit</button>
      </div>
    )    
  }
}

const Question = ({id, text, answers, chosen, setAnswer}) => {
  return (
    <div>
      <p>{text}</p>
      {answers.map((answer, index) => (
        <div key={index}>
          <label>
            <input type="radio" checked={index === chosen} name={text + id} onChange={() => setAnswer(index)}/>
            {answer}
          </label>
        </div>
        )
      )}
    </div>
  )
}

const questions = [
  {
    text: "first",
    id: 1,
    answers: [
      "a",
      "b",
      "c"
    ]
  },
  {
    text: "second",
    id: 2,
    answers: [
      "d",
      "e",
      "f"
    ]
  },
  {
    text: "third",
    id: 3,
    answers: [
      "g",
      "h",
      "i"
    ]
  },
]

const App = () => {
  const [userName, setUserName] = useState("")
  const [answers, setAnswers] = useState(questions.map(() => -1));
  const onSubmit = (answers) => alert(userName + '\'s answers are: ' + answers.join(' '))

  return (
    <div>
      <label>Name: <input type="text" value={userName} onChange={(e) => setUserName(e.target.value)}/></label>
      <Test answers={answers} setAnswers={setAnswers} questions={questions}/>
      <button disabled={userName === ""} onClick={onSubmit}>Submit</button>
    </div>
  )
}

export default App;
